import { useState } from "react";
import ExpenseItem from "./components/ExpenseItem";
import Expenses from "./components/Expenses";

function App() {
  const expenses = [
    {
      id: "e1",
      title: "CSE8A",
      amount: "Available",
      date: new Date(2021, 2, 28),
    },
    {
      id: "e2",
      title: "CSE8B",
      amount: "Available",
      date: new Date(2022, 2, 17),
    },
    {
      id: "e3",
      title: "CSE11",
      amount: "Available",
      date: new Date(2023, 2, 15),
    },
  ];

  const [searchValue, setSearchValue] = useState("");

  const handleSearchChange = (event) => {
    setSearchValue(event.target.value);
  };

  const filteredExpenses = expenses.filter((expense) =>
    expense.title.toLowerCase().includes(searchValue.toLowerCase())
  );

  return (
    <div>
      <div style={{ display: "flex", justifyContent: "center", background: "#f0f0f0", padding: "10px" }}>
        <h2 style={{ marginRight: "20px" }}>CourseHub</h2>
        <div style={{ borderRight: "1px solid #ccc", paddingRight: "20px", marginRight: "20px" }}></div>
        <button style={{ marginRight: "20px" }}>Home</button>
        <div style={{ borderRight: "1px solid #ccc", paddingRight: "20px", marginRight: "20px" }}></div>
        <button style={{ marginRight: "20px" }}>Course Planner</button>
        <div style={{ borderRight: "1px solid #ccc", paddingRight: "20px", marginRight: "20px" }}></div>
        <button>Github</button>
      </div>
      <div style={{ display: "flex", justifyContent: "center", marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Search..."
          value={searchValue}
          onChange={handleSearchChange}
          style={{
            padding: "10px",
            borderRadius: "20px",
            border: "1px solid #ccc",
            width: "300px",
          }}
        />
      </div>
      <Expenses items={filteredExpenses} />
    </div>
  );
}

export default App;
